package OOP;

public class OOP_BangDiem{
    public String masv;
    public String ten;
    public String mamon;
    public String diem;
  public OOP_BangDiem() {
	// TODO Auto-generated constructor stub
  }
	public OOP_BangDiem(String masv, String ten, String mamon, String diem) {
	
		this.masv = masv;
		this.ten = ten;
		this.mamon = mamon;
		this.diem = diem;
	}
	public String getMasv() {
		return masv;
	}
	public void setMasv(String masv) {
		this.masv = masv;
	}
	public String getTen() {
		return ten;
	}
	public void setTen(String ten) {
		this.ten = ten;
	}
	public String getMamon() {
		return mamon;
	}
	public void setMamon(String mamon) {
		this.mamon = mamon;
	}
	public String getDiem() {
		return diem;
	}
	public void setDiem(String diem) {
		this.diem = diem;
	}
}
