package OOP;

public class OOP_Sinhvien_Lichhoc {
	String masv;
	String ngayhoc;
	String cahoc;
	String thoigian;
	String malichhoc;
	String phonghoc;
	String mamonhoc;
	public OOP_Sinhvien_Lichhoc() {
		// TODO Auto-generated constructor stub
	}
	
	public OOP_Sinhvien_Lichhoc(String masv, String ngayhoc, String cahoc, String thoigian, String malichhoc,
			String phonghoc, String mamonhoc) {
		this.masv = masv;
		this.ngayhoc = ngayhoc;
		this.cahoc = cahoc;
		this.thoigian = thoigian;
		this.malichhoc = malichhoc;
		this.phonghoc = phonghoc;
		this.mamonhoc = mamonhoc;
	}

	public String getMasv() {
		return masv;
	}
	public void setMasv(String masv) {
		this.masv = masv;
	}
	public String getNgayhoc() {
		return ngayhoc;
	}
	public void setNgayhoc(String ngayhoc) {
		this.ngayhoc = ngayhoc;
	}
	public String getCahoc() {
		return cahoc;
	}
	public void setCahoc(String cahoc) {
		this.cahoc = cahoc;
	}
	public String getThoigian() {
		return thoigian;
	}
	public void setThoigian(String thoigian) {
		this.thoigian = thoigian;
	}
	public String getMalichhoc() {
		return malichhoc;
	}
	public void setMalichhoc(String malichhoc) {
		this.malichhoc = malichhoc;
	}
	public String getPhonghoc() {
		return phonghoc;
	}
	public void setPhonghoc(String phonghoc) {
		this.phonghoc = phonghoc;
	}
	public String getMamonhoc() {
		return mamonhoc;
	}
	public void setMamonhoc(String mamonhoc) {
		this.mamonhoc = mamonhoc;
	}
	
}
